import React, { useState, useEffect } from 'react';

const PreCloseLoan = ({ loan, onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    principalAmount: loan?.loanAmount || 0,
    interestPaid: loan?.totalCollected || 0,
    remainingPrincipal: loan?.remainingAmount || 0,
    penaltyAmount: 0,
    discountAmount: 0,
    finalAmount: 0,
    paymentMethod: 'cash',
    notes: ''
  });

  const [calculations, setCalculations] = useState({
    totalOutstanding: 0,
    penaltyApplied: 0,
    discountApplied: 0,
    finalSettlement: 0,
    savings: 0
  });

  useEffect(() => {
    calculateFinalAmount();
  }, [formData]);

  const calculateFinalAmount = () => {
    const totalOutstanding = formData.remainingPrincipal + formData.penaltyAmount;
    const afterDiscount = totalOutstanding - formData.discountAmount;
    const finalSettlement = Math.max(0, afterDiscount);
    const savings = formData.discountAmount;

    setCalculations({
      totalOutstanding,
      penaltyApplied: formData.penaltyAmount,
      discountApplied: formData.discountAmount,
      finalSettlement,
      savings
    });

    setFormData(prev => ({
      ...prev,
      finalAmount: finalSettlement
    }));
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (window.confirm(`Are you sure you want to pre-close this loan for ₹${calculations.finalSettlement.toLocaleString()}?`)) {
      try {
        const response = await fetch(`http://localhost:5000/api/loans/${loan._id}/pre-close`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            paymentMethod: formData.paymentMethod,
            finalAmount: calculations.finalSettlement,
            penaltyAmount: formData.penaltyAmount,
            discountAmount: formData.discountAmount,
            notes: formData.notes
          }),
        });
        
        if (response.ok) {
          alert('Loan pre-closed successfully!');
          onSuccess();
          onClose();
        }
      } catch (error) {
        console.error('Error pre-closing loan:', error);
        alert('Error pre-closing loan. Please try again.');
      }
    }
  };

  if (!loan) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-xl shadow-2xl max-w-4xl w-full max-h-[90vh] overflow-y-auto">
        <div className="card-header">
          <div className="flex justify-between items-center">
            <div>
              <h2 className="text-xl font-bold text-gray-900">Pre-Close Loan</h2>
              <p className="text-sm text-gray-500 mt-1">
                Client: {loan.client?.name} | Loan Amount: ₹{loan.loanAmount.toLocaleString()}
              </p>
            </div>
            <button 
              onClick={onClose}
              className="text-gray-400 hover:text-gray-600 text-2xl"
            >
              ×
            </button>
          </div>
        </div>

        <div className="card-body">
          <form onSubmit={handleSubmit} className="space-y-6">
            {/* Loan Summary */}
            <div className="bg-gray-50 p-4 rounded-lg">
              <h3 className="font-semibold text-gray-900 mb-3">Loan Summary</h3>
              <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                <div>
                  <span className="text-gray-500">Principal Amount:</span>
                  <div className="font-semibold">₹{loan.loanAmount.toLocaleString()}</div>
                </div>
                <div>
                  <span className="text-gray-500">Interest Collected:</span>
                  <div className="font-semibold text-green-600">₹{loan.totalCollected.toLocaleString()}</div>
                </div>
                <div>
                  <span className="text-gray-500">Remaining Principal:</span>
                  <div className="font-semibold">₹{loan.remainingAmount.toLocaleString()}</div>
                </div>
                <div>
                  <span className="text-gray-500">Monthly Interest:</span>
                  <div className="font-semibold">₹{loan.monthlyInterest.toLocaleString()}</div>
                </div>
              </div>
            </div>

            {/* Settlement Details */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label className="form-label">Penalty Amount (₹)</label>
                <input
                  type="number"
                  name="penaltyAmount"
                  value={formData.penaltyAmount}
                  onChange={handleInputChange}
                  className="form-input"
                  placeholder="0"
                  min="0"
                />
                <p className="text-xs text-gray-500 mt-1">
                  Early closure penalty (if applicable)
                </p>
              </div>

              <div>
                <label className="form-label">Discount Amount (₹)</label>
                <input
                  type="number"
                  name="discountAmount"
                  value={formData.discountAmount}
                  onChange={handleInputChange}
                  className="form-input"
                  placeholder="0"
                  min="0"
                />
                <p className="text-xs text-gray-500 mt-1">
                  Discount for early payment
                </p>
              </div>
            </div>

            {/* Payment Method */}
            <div>
              <label className="form-label">Payment Method</label>
              <select
                name="paymentMethod"
                value={formData.paymentMethod}
                onChange={handleInputChange}
                className="form-select"
              >
                <option value="cash">💵 Cash</option>
                <option value="bank_transfer">🏦 Bank Transfer</option>
                <option value="cheque">📄 Cheque</option>
              </select>
            </div>

            {/* Notes */}
            <div>
              <label className="form-label">Notes</label>
              <textarea
                name="notes"
                value={formData.notes}
                onChange={handleInputChange}
                className="form-textarea"
                rows="3"
                placeholder="Additional notes about this pre-closure..."
              />
            </div>

            {/* Calculation Summary */}
            <div className="bg-blue-50 p-4 rounded-lg border border-blue-200">
              <h3 className="font-semibold text-blue-900 mb-3">Settlement Calculation</h3>
              <div className="space-y-2 text-sm">
                <div className="flex justify-between">
                  <span>Remaining Principal:</span>
                  <span className="font-medium">₹{loan.remainingAmount.toLocaleString()}</span>
                </div>
                <div className="flex justify-between">
                  <span>Penalty Amount:</span>
                  <span className="font-medium text-red-600">+₹{formData.penaltyAmount.toLocaleString()}</span>
                </div>
                <div className="flex justify-between">
                  <span>Discount Amount:</span>
                  <span className="font-medium text-green-600">-₹{formData.discountAmount.toLocaleString()}</span>
                </div>
                <hr className="border-blue-200" />
                <div className="flex justify-between text-lg font-bold">
                  <span>Final Settlement Amount:</span>
                  <span className="text-blue-900">₹{calculations.finalSettlement.toLocaleString()}</span>
                </div>
                {formData.discountAmount > 0 && (
                  <div className="flex justify-between text-sm text-green-600">
                    <span>Client Savings:</span>
                    <span>₹{calculations.savings.toLocaleString()}</span>
                  </div>
                )}
              </div>
            </div>

            {/* Action Buttons */}
            <div className="flex justify-end space-x-3 pt-4 border-t border-gray-200">
              <button 
                type="button"
                className="btn btn-outline"
                onClick={onClose}
              >
                Cancel
              </button>
              <button 
                type="submit" 
                className="btn btn-danger"
              >
                🔒 Pre-Close Loan (₹{calculations.finalSettlement.toLocaleString()})
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default PreCloseLoan;
