import React, { useState, useEffect } from 'react';

const LoanManagement = () => {
    const [loans, setLoans] = useState([]);
    const [clients, setClients] = useState([]);
    const [showForm, setShowForm] = useState(false);
    const [formData, setFormData] = useState({
        clientId: '',
        loanAmount: '',
        interestRate: '',
        notes: ''
    });

    useEffect(() => {
        fetchLoans();
        fetchClients();
    }, []);

    const fetchLoans = async () => {
        try {
            const response = await fetch('http://localhost:5000/api/loans');
            const data = await response.json();
            setLoans(data);
        } catch (error) {
            console.error('Error fetching loans:', error);
        }
    };

    const fetchClients = async () => {
        try {
            const response = await fetch('http://localhost:5000/api/clients');
            const data = await response.json();
            setClients(data);
        } catch (error) {
            console.error('Error fetching clients:', error);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('http://localhost:5000/api/loans', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });

            if (response.ok) {
                setFormData({ clientId: '', loanAmount: '', interestRate: '', notes: '' });
                setShowForm(false);
                fetchLoans();
            }
        } catch (error) {
            console.error('Error creating loan:', error);
        }
    };

    const handleInputChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const collectInterest = async (loanId) => {
        try {
            const response = await fetch(`http://localhost:5000/api/loans/${loanId}/collect-interest`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ paymentMethod: 'cash' }),
            });

            if (response.ok) {
                fetchLoans();
                alert('Interest collected successfully!');
            }
        } catch (error) {
            console.error('Error collecting interest:', error);
        }
    };

    const preCloseLoan = async (loanId) => {
        if (window.confirm('Are you sure you want to pre-close this loan?')) {
            try {
                const response = await fetch(`http://localhost:5000/api/loans/${loanId}/pre-close`, {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ paymentMethod: 'cash' }),
                });

                if (response.ok) {
                    fetchLoans();
                    alert('Loan pre-closed successfully!');
                }
            } catch (error) {
                console.error('Error pre-closing loan:', error);
            }
        }
    };

    return (
        <div>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
                <h1>üí∞ Loan Management</h1>
                <button className="btn" onClick={() => setShowForm(!showForm)}>
                    {showForm ? '‚ùå Cancel' : '‚ûï Create New Loan'}
                </button>
            </div>

            {showForm && (
                <div className="card">
                    <h2>Create New Loan</h2>
                    <form onSubmit={handleSubmit}>
                        <div className="form-group">
                            <label>Select Client</label>
                            <select
                                name="clientId"
                                value={formData.clientId}
                                onChange={handleInputChange}
                                required
                            >
                                <option value="">Choose a client...</option>
                                {clients.map((client) => (
                                    <option key={client._id} value={client._id}>
                                        {client.name} - {client.email}
                                    </option>
                                ))}
                            </select>
                        </div>

                        <div className="form-group">
                            <label>Loan Amount (‚Çπ)</label>
                            <input
                                type="number"
                                name="loanAmount"
                                value={formData.loanAmount}
                                onChange={handleInputChange}
                                required
                                min="1"
                            />
                        </div>

                        <div className="form-group">
                            <label>Interest Rate (% per month)</label>
                            <input
                                type="number"
                                name="interestRate"
                                value={formData.interestRate}
                                onChange={handleInputChange}
                                required
                                min="0"
                                step="0.1"
                            />
                        </div>

                        <div className="form-group">
                            <label>Notes</label>
                            <textarea
                                name="notes"
                                value={formData.notes}
                                onChange={handleInputChange}
                                placeholder="Additional notes about this loan..."
                            />
                        </div>

                        <button type="submit" className="btn">üíæ Create Loan</button>
                    </form>
                </div>
            )}

            <div className="card">
                <h2>Active Loans</h2>
                {loans.length > 0 ? (
                    <table className="table">
                        <thead>
                            <tr>
                                <th>Client</th>
                                <th>Loan Amount</th>
                                <th>Interest Rate</th>
                                <th>Monthly Interest</th>
                                <th>Status</th>
                                <th>Total Collected</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {loans.map((loan) => (
                                <tr key={loan._id}>
                                    <td>{loan.client?.name || 'N/A'}</td>
                                    <td>‚Çπ{loan.loanAmount.toLocaleString()}</td>
                                    <td>{loan.interestRate}%</td>
                                    <td>‚Çπ{loan.monthlyInterest.toLocaleString()}</td>
                                    <td>
                                        <span className={`status-${loan.status}`}>
                                            {loan.status}
                                        </span>
                                    </td>
                                    <td>‚Çπ{loan.totalCollected.toLocaleString()}</td>
                                    <td>
                                        {loan.status === 'active' && (
                                            <>
                                                <button
                                                    className="btn btn-success"
                                                    style={{ padding: '0.3rem 0.8rem', fontSize: '0.8rem', marginRight: '0.3rem' }}
                                                    onClick={() => collectInterest(loan._id)}
                                                >
                                                    üí∞ Collect Interest
                                                </button>
                                                <button
                                                    className="btn btn-danger"
                                                    style={{ padding: '0.3rem 0.8rem', fontSize: '0.8rem' }}
                                                    onClick={() => preCloseLoan(loan._id)}
                                                >
                                                    üîí Pre-Close
                                                </button>
                                            </>
                                        )}
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                ) : (
                    <p>No loans found. Create your first loan above!</p>
                )}
            </div>
        </div>
    );
};

export default LoanManagement;
