import React, { useState, useEffect } from 'react';

const Dashboard = () => {
    const [stats, setStats] = useState({
        totalClients: 0,
        activeLoans: 0,
        totalLoanAmount: 0,
        monthlyIncome: 0
    });

    const [recentTransactions, setRecentTransactions] = useState([]);

    useEffect(() => {
        // Fetch dashboard data
        fetchDashboardData();
    }, []);

    const fetchDashboardData = async () => {
        try {
            // Fetch clients
            const clientsResponse = await fetch('http://localhost:5000/api/clients');
            const clients = await clientsResponse.json();

            // Fetch loans
            const loansResponse = await fetch('http://localhost:5000/api/loans');
            const loans = await loansResponse.json();

            // Fetch income summary
            const incomeResponse = await fetch('http://localhost:5000/api/transactions/summary/income');
            const incomeData = await incomeResponse.json();

            // Fetch recent transactions
            const transactionsResponse = await fetch('http://localhost:5000/api/transactions');
            const transactions = await transactionsResponse.json();

            setStats({
                totalClients: clients.length,
                activeLoans: loans.filter(loan => loan.status === 'active').length,
                totalLoanAmount: loans.reduce((sum, loan) => sum + loan.loanAmount, 0),
                monthlyIncome: incomeData.monthlyIncome
            });

            setRecentTransactions(transactions.slice(0, 5));
        } catch (error) {
            console.error('Error fetching dashboard data:', error);
        }
    };

    return (
        <div>
            <h1>📊 Dashboard</h1>

            <div className="stats-grid">
                <div className="stat-card">
                    <h3>Total Clients</h3>
                    <div className="number">{stats.totalClients}</div>
                    <p>Active clients in system</p>
                </div>

                <div className="stat-card">
                    <h3>Active Loans</h3>
                    <div className="number">{stats.activeLoans}</div>
                    <p>Currently active loans</p>
                </div>

                <div className="stat-card">
                    <h3>Total Loan Amount</h3>
                    <div className="number">₹{stats.totalLoanAmount.toLocaleString()}</div>
                    <p>Total amount disbursed</p>
                </div>

                <div className="stat-card">
                    <h3>Monthly Income</h3>
                    <div className="number">₹{stats.monthlyIncome.toLocaleString()}</div>
                    <p>Interest collected this month</p>
                </div>
            </div>

            <div className="card">
                <h2>📋 Recent Transactions</h2>
                {recentTransactions.length > 0 ? (
                    <table className="table">
                        <thead>
                            <tr>
                                <th>Date</th>
                                <th>Client</th>
                                <th>Type</th>
                                <th>Amount</th>
                                <th>Description</th>
                            </tr>
                        </thead>
                        <tbody>
                            {recentTransactions.map((transaction, index) => (
                                <tr key={index}>
                                    <td>{new Date(transaction.transactionDate).toLocaleDateString()}</td>
                                    <td>{transaction.client?.name || 'N/A'}</td>
                                    <td>
                                        <span className={`status-${transaction.type === 'interest' ? 'active' : 'closed'}`}>
                                            {transaction.type}
                                        </span>
                                    </td>
                                    <td>₹{transaction.amount.toLocaleString()}</td>
                                    <td>{transaction.description}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                ) : (
                    <p>No recent transactions found.</p>
                )}
            </div>
        </div>
    );
};

export default Dashboard;
